import { useNavigate, useParams } from "react-router-dom";
import { useState } from "react";
import useFetch from "../hooks/useFetch";
import contactImgUrl from "/Users/haidar/Desktop/crud-app/client/src/images/user.png"


export default function ContactDetails() {
    let { contactId } = useParams();
    const navigate = useNavigate();
    const url = `http://localhost:3001/getUsers${contactId}`;
    const [data, error] = useFetch(url)
    const [updateContactData, setUpdateContactData] = useState(data);
    const [editing, setEditing] = useState(false);

    useFetch(() => {
        setUpdateContactData(data);
        console.log(updateContactData)
    }, [data])

    return (
        <>
            <nav className="md:w-3/4 m-auto mt-8">
                <button
                    aria-label="Navigates back to the home page"
                    onClick={() => navigate("/")}
                >

                    <svg
                        xmlns="http://www.w3.org/2000/svg"
                        height="48px"
                        viewBox="0 0 24 24"
                        width="48px"
                        fill="#000000"
                    >
                        <path d="M0 0h24v24H0v0z" fill="none" />
                        <path d="M20 11H7.83l5.59-5.59L12 4l-8 8 8 8 1.41-1.41L7.83 13H20v-2z" />
                    </svg>
                </button>
            </nav>

            {error !== null ? (
                <div className="bg-red-100 border border-red-400 text-red-700 md:2/5 lg:max-w-md px-4 py-3 rounded-md m-auto mt-24"
                role="alert"
                >
                    <P className="text-xl text-center">
                        <strong>Sorry</strong>It seems that htis contact is missing or has been deleted
                    </P>

                </div>
            ): (
                <div className="flex flex-col items-center my-16 mx-2 md">

                </div>
            )}




        </>

    )

}
